import React, { Component } from 'react';
import {Link } from "react-router-dom";
import './Dashboard.css';
import { Col, Row, Container , Button, Card, Form, FormControl} from "react-bootstrap";
import {Doughnut, Radar} from 'react-chartjs-2';
import PersonanlityInfo from "../PersonalityInfo.json";
import History from "../History";
import {Cities} from "../cities.jsx";
import EducationInfo from "../EducationInfo"; 

const Score =  PersonanlityInfo.PersonalInfo.map((PerInfo) => 
                    PerInfo.PersonalityScore
                );

console.log(Score);

const dataa ={
    labels: ['Extroversion', 'Emotion Stability', 'Agreeableness', 'Conscientiousness', 'Intellect/ Imagination'],
    datasets:
    [{ 
    label: "",
    backgroundColor: "rgba(241, 90, 34, 0.2)",
    data: Score

        }]
    };


class RecommendProg extends Component{
  render(){

      return(
          <>
            <div>
                <h1 className = "edu-navbar-logo">
                <i className = "fas fa-graduation-cap edu-cap" style={{ fontSize:'5vw'}}></i>
                    <p className= "edu-navbar-p" style={{ fontSize:'1.25vw'}}>Study Program</p><p className="edu-navbar-p" style={{ fontSize:'1.25vw'}}>Recommendation</p><p className="edu-navbar-p" style={{ fontSize:'1.25vw'}}>System
                </p></h1>
            </div>

        <Container>
            <Row >
                <Col style={{ border:'solid', borderWidth:'thin'}}>
                    <div >
                    <p style={{ fontSize:'1.5vw', textDecoration:'bold'}} >Result History</p>
                    { History.RecommendProgList.map((RecInfo) => {
                        return(
                            <div style={{border:'solid', borderWidth:'thin', paddingRight:'5%', paddingButtom:'5%', margin:'3%'}}>
                            <p>Date</p>   
                            <Row className= "history-proglist"  style={{ fontSize:'1.2vw'}}>{RecInfo.Date}</Row>
                                {
                                RecInfo.ProgramList.map((RecDetail) => {
                                    return (
                                    <div>
                                        <Row style={{ fontSize:'1vw'}} className="history-recprogram" >{RecDetail.Programs}</Row>
                                        {
                                            RecInfo.ProgramList.map((RecDetail) => {
                                            return (
                                                <Row className="history-recscore" style={{ fontSize:'1vw'}}>
                                                    {RecDetail.RecProgScore}
                                                </Row>
                                            );
                                            }) 

                                        }

                                    </div>
                                    );
                                })
                                } 
                            </div>
                                );
                            })
                            }
                    </div>
        </Col>

        <Col >
        <div style={{ border:'solid',borderRadius:'20px', borderWidth:'thin',marginLeft:'5%', padding:'10%'}}>
        <p style={{ fontSize:'1.5vw', textDecoration:'bold'}} >Give us some feedback!</p>
        <Form.Group>
        <FormControl as="select"  onChange={(e) => this.handleCities(e)}>
                {Cities.cities && Cities.cities.map((e, key) => {
                return <option key={key} value={e.Key}>{e.Value}</option>
            })}
        </FormControl> 
        <Form.Control style={{ fontSize:'1vw', height:"100px", marginTop:"4%"}} type="text" name="feedback"
               placeholder="Your Feedback..."/>
        </Form.Group>



        </div>

        </Col>

        </Row>
        </Container>

        <Container>
                <Col style={{marginTop:'20%', border:'solid', borderStyle:'thin'}}>
                <div>
                {
                  EducationInfo.EduInfo.map((Info) => {
                    return (
                      <div>
                        <h4 style={{ fontSize:'1vw'}} >{Info.TestName}</h4>
                        <ul>
                          {
                            Info.ScoreList.map((InfoDetail) => {
                              return (
                                  <p style={{ fontSize:'1vw'}} >
                                    {InfoDetail.Score}
                                  </p>
                              );
                            })
                          }
                        </ul>
                      </div>
                    );
                  })
                } 
            </div>

        </Col>
                <Col>
                    <Radar style="responsive:true;" data={dataa} />
                </Col>

    </Container>


        <br/>
        <Link to="/RecProgram">
                <Button className="Dashboard-done-button" style={{ fontSize:'1vw'}}>
                    Done
                </Button>
            </Link>




          </>
      )
  }
}

export default RecommendProg;
