import React, { Component } from 'react';
import {Link } from "react-router-dom";
import './EduInformation.css'
import EduInfoLabel from "../EduInfoLabel"; 
import PersonalityTest from "../PersonalityTest.json"
import { Col,Row, Container , Button, Form, Navbar} from "react-bootstrap";

class EduInformation extends Component{
  constructor() {
    super();
    this.state = {
      input: {},
      errors: {}
    };

    // this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  handleChange(event) {
    // event.preventDefault();
    let input = this.state.input;
    input[event.target.name] = event.target.value;

    this.setState({
      input
    });
  }
  handleSubmit(event) {
    event.preventDefault();

    if(this.validate()){
        console.log(this.state);

        let input = {};
        input["username"] = "";
        input["last_name"]= "";
        input["email"] = "";
        input["password"] = "";
        input["confirm_password"] = "";
        this.setState({input:input});

        alert('Demo Form is submited');
    }
  }

  validate(){
      let input = this.state.input;
      let errors = {};
      let isValid = true;

      if (!input["username"]) {
        isValid = false;
        errors["username"] = "Please enter your username.";
      }
      if (!input["last_name"]) {
        isValid = false;
        errors["last_name"] = "Please enter your last name.";
      }

      if (!input["email"]) {
        isValid = false;
        errors["email"] = "Please enter your email Address.";
      }

      if (typeof input["email"] !== "undefined") {

        var pattern = new RegExp(/^(("[\w-\s]+")|([\w-]+(?:\.[\w-]+)*)|("[\w-\s]+")([\w-]+(?:\.[\w-]+)*))(@((?:[\w-]+\.)*\w[\w-]{0,66})\.([a-z]{2,6}(?:\.[a-z]{2})?)$)|(@\[?((25[0-5]\.|2[0-4][0-9]\.|1[0-9]{2}\.|[0-9]{1,2}\.))((25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\.){2}(25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\]?$)/i);
        if (!pattern.test(input["email"])) {
          isValid = false;
          errors["email"] = "Please enter valid email address.";
        }
      }

      if (!input["password"]) {
        isValid = false;
        errors["password"] = "Please enter your password.";
      }

      if (!input["confirm_password"]) {
        isValid = false;
        errors["confirm_password"] = "Please enter your confirm password.";
      }

      if (typeof input["password"] !== "undefined" && typeof input["confirm_password"] !== "undefined") {

        if (input["password"] !== input["confirm_password"]) {
          isValid = false;
          errors["password"] = "Passwords don't match.";
        }
      } 

      this.setState({
        errors: errors
      });

      return isValid;
  }

  render(){


      return(
          <>
          <div>


             {/* <h1 className = "edu-navbar-logo">
             <i className = "fas fa-graduation-cap edu-cap" style={{ fontSize:'5vw'}}></i>
                 <p className= "edu-navbar-p" style={{ fontSize:'1.25vw'}}>Study Program</p><p className="edu-navbar-p" style={{ fontSize:'1.25vw'}}>Recommendation</p><p className="edu-navbar-p" style={{ fontSize:'1.25vw'}}>System
             </p></h1> */}
               <Navbar collapseOnSelect expand="lg"  >
            <Navbar.Brand href="/"  >
                <i 
                 className = "fas fa-graduation-cap" style={{ fontSize:'4.95vw'}}
                ></i>
                    <p 
                    className= "navbar-p" style={{ fontSize:'1vw'}}
                    >Study Program</p><p 
                    className="navbar-p" style={{ fontSize:'1vw'}}
                    >Recommendation</p>
                    <p 
                    className="navbar-p" style={{ fontSize:'1vw'}}
                    >System
                </p>
                </Navbar.Brand>
            </Navbar>


    <Container>
      <Col>
             <h1 className= "Edu-header" style={{ fontSize:'2vw'}}>Personality Test</h1>
             <div>



   <ul>

    <Col className= "edu-box">
                {
                  PersonalityTest.PersonalityTestList.map((Info) => {
                    return (
                      <Col md={{ span: 8, offset: 3 }}
                      // className= "edu-input-box" 
                      >
                        <Row>
                        <h4 className= "edu-info-label" style={{ fontSize:'1.4vw', textAlign:'left'}} >{Info.Question}</h4>
                        </Row>
                        <Row >
                        <ul>
                          {
                            // Info.ScoreList.map((InfoDetail) => {
                            //   return (

                                <Form onSubmit={this.handleSubmit}>
                                      {['radio'].map((type) => (
                                        <div key={`inline-${type}`} className="mb-3" >
                                          <fieldset >
                                          <Form.Group  name="inputt">
                                          <Form.Check 
                                          inline label="1" type={type} id={`inline-${type}-1`} value="1" style={{ fontSize:'1.5vw'}}
                                          name="formHorizontalRadios" />
                                          <Form.Check  
                                          inline label="2" type={type} id={`inline-${type}-2`}  value="2" style={{ fontSize:'1.25vw'}}
                                          name="formHorizontalRadios"/>
                                          <Form.Check                             
                                          inline label="3" type={type} id={`inline-${type}-3`} value="3" style={{ fontSize:'1.25vw'}}
                                          name="formHorizontalRadios" />
                                          <Form.Check                                  
                                          inline label="4" type={type} id={`inline-${type}-4`} value="4" style={{ fontSize:'1.25vw'}}
                                          name="formHorizontalRadios" />
                                          <Form.Check                                  
                                          inline label="5" type={type} id={`inline-${type}-5`} value="5" style={{ fontSize:'1.25vw'}}
                                          name="formHorizontalRadios" />
                                          </Form.Group>
                                          </fieldset>
                                        </div>
                                      ))}
                                    </Form>


                              // );
                            // })
                          }
                        </ul>
                        </Row>
                    </Col>
                    );
                  })
                } 

        <Link to="/RecProgram">
                <Button className="Edu-done-button" style={{ fontSize:'1vw', float:'right'}}>
                  Done
                </Button>
                </Link>

          </Col>
            </ul>






             </div>
             </Col>
            </Container>
          </div>
          </>
      )
  }
}

export default EduInformation;
