import React, { Component } from 'react';
import {Link } from "react-router-dom";
import './AccInfo.css';
import { Col,Row, Container , Image, Form, Button} from "react-bootstrap";


class AccInfo extends React.Component {
    constructor() {
    super();
    this.state = {
      input: {},
      errors: {}
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    let input = this.state.input;
    input[event.target.name] = event.target.value;

    this.setState({
      input
    });
  }

  handleSubmit(event) {
    event.preventDefault();

    if(this.validate()){
        console.log(this.state);

        let input = {};
        input["username"] = "";
        input["last_name"]= "";
        input["email"] = "";
        input["password"] = "";
        input["confirm_password"] = "";
        this.setState({input:input});

        alert('Demo Form is submited');
    }
  }

  validate(){
      let input = this.state.input;
      let errors = {};
      let isValid = true;

      if (!input["username"]) {
        isValid = false;
        errors["username"] = "Please enter your username.";
      }
      if (!input["last_name"]) {
        isValid = false;
        errors["last_name"] = "Please enter your last name.";
      }

      if (!input["email"]) {
        isValid = false;
        errors["email"] = "Please enter your email Address.";
      }

      if (typeof input["email"] !== "undefined") {

        var pattern = new RegExp(/^(("[\w-\s]+")|([\w-]+(?:\.[\w-]+)*)|("[\w-\s]+")([\w-]+(?:\.[\w-]+)*))(@((?:[\w-]+\.)*\w[\w-]{0,66})\.([a-z]{2,6}(?:\.[a-z]{2})?)$)|(@\[?((25[0-5]\.|2[0-4][0-9]\.|1[0-9]{2}\.|[0-9]{1,2}\.))((25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\.){2}(25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\]?$)/i);
        if (!pattern.test(input["email"])) {
          isValid = false;
          errors["email"] = "Please enter valid email address.";
        }
      }

      if (!input["password"]) {
        isValid = false;
        errors["password"] = "Please enter your password.";
      }

      if (!input["confirm_password"]) {
        isValid = false;
        errors["confirm_password"] = "Please enter your confirm password.";
      }

      if (typeof input["password"] !== "undefined" && typeof input["confirm_password"] !== "undefined") {

        if (input["password"] !== input["confirm_password"]) {
          isValid = false;
          errors["password"] = "Passwords don't match.";
        }
      } 

      this.setState({
        errors: errors
      });

      return isValid;
  }

  render() {
    return (
      <div>
                <h1 className = "edu-navbar-logo">
                <i className = "fas fa-graduation-cap edu-cap"></i>
                    <p className= "edu-navbar-p">Study Program</p><p className="edu-navbar-p">Recommendation</p><p className="edu-navbar-p">System
                </p></h1>
          <Container>
            <Col></Col>
            <Col>
            {/* <Row>  */}
                <h1 style={{ fontSize:'2vw'}}>User Information</h1>
            {/* </Row> */}

          <Form onSubmit={this.handleSubmit}>

            <Form.Group >
              <Form.Label style={{ fontSize:'1vw'}}>First Name</Form.Label>
              <Form.Control style={{ fontSize:'1vw'}} type="text" placeholder="Enter First Name"  onChange={this.handleChange} />
            </Form.Group>

            <Form.Group >
              <Form.Label style={{ fontSize:'1vw'}}>Last Name</Form.Label>
              <Form.Control style={{ fontSize:'1vw'}} type="email" placeholder="Enter Last Name"  onChange={this.handleChange} />
            </Form.Group>


          <Form.Label style={{ fontSize:'1vw'}}>Gender</Form.Label>
            <Form.Group  >
              <Form.Control as="select" defaultValue="" style={{ fontSize:'1vw'}}>
               <option style={{ fontSize:'1vw'}}>F</option>
               <option style={{ fontSize:'1vw'}}>M</option>
              </Form.Control>
            </Form.Group>

            <Form.Label style={{ fontSize:'1vw'}}>Nationality</Form.Label>
            <Form.Group  >
              <Form.Control as="select" defaultValue="" style={{ fontSize:'1vw'}}>
               <option style={{ fontSize:'1vw'}}>F</option>
               <option style={{ fontSize:'1vw'}}>M</option>
              </Form.Control>
            </Form.Group>






            <h1 style={{ fontSize:'2vw'}}>Account Information</h1>            
            <Form.Group >
              <Form.Label style={{ fontSize:'1vw'}}>First Name</Form.Label>
              <Form.Control style={{ fontSize:'1vw'}} type="text" placeholder="Enter First Name"  onChange={this.handleChange} />
            </Form.Group>

            <Form.Group >
              <Form.Label style={{ fontSize:'1vw'}}>Last Name</Form.Label>
              <Form.Control style={{ fontSize:'1vw'}} type="email" placeholder="Enter Last Name"  onChange={this.handleChange} />
            </Form.Group>


          <Form.Label style={{ fontSize:'1vw'}}>Gender</Form.Label>
            <Form.Group  >
              <Form.Control as="select" defaultValue="" style={{ fontSize:'1vw'}}>
               <option style={{ fontSize:'1vw'}}>F</option>
               <option style={{ fontSize:'1vw'}}>M</option>
              </Form.Control>
            </Form.Group>

            <Form.Label style={{ fontSize:'1vw'}}>Nationality</Form.Label>
            <Form.Group  >
              <Form.Control as="select" defaultValue="" style={{ fontSize:'1vw'}}>
               <option style={{ fontSize:'1vw'}}>F</option>
               <option style={{ fontSize:'1vw'}}>M</option>
              </Form.Control>
            </Form.Group>





















            <Button 
            style={{display:"flex", alignItems:"center", justifyContent:"center", fontSize:"1vw"}}
            variant="primary" type="submit">
              Sign Up
            </Button>
           </Form>
           </Col>
           <Col></Col>
          </Container>



      </div>
    );
  }
}

export default AccInfo;
