import React from 'react';
import './SignUp.css'
// import {Link} from 'react-router-dom';
import bg from '../images/IMG_0947.JPG'; 
import { Col,Row, Container , Image, Jumbotron, Button, Form} from "react-bootstrap";


class SignUp extends React.Component {
    constructor() {
    super();
    this.state = {
      input: {},
      errors: {}
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    let input = this.state.input;
    input[event.target.name] = event.target.value;

    this.setState({
      input
    });
  }

  handleSubmit(event) {
    event.preventDefault();

    if(this.validate()){
        console.log(this.state);

        let input = {};
        input["username"] = "";
        input["last_name"]= "";
        input["email"] = "";
        input["password"] = "";
        input["confirm_password"] = "";
        this.setState({input:input});

        alert('Demo Form is submited');
    }
  }

  validate(){
      let input = this.state.input;
      let errors = {};
      let isValid = true;

      if (!input["username"]) {
        isValid = false;
        errors["username"] = "Please enter your username.";
      }
      if (!input["last_name"]) {
        isValid = false;
        errors["last_name"] = "Please enter your last name.";
      }

      if (!input["email"]) {
        isValid = false;
        errors["email"] = "Please enter your email Address.";
      }

      if (typeof input["email"] !== "undefined") {

        var pattern = new RegExp(/^(("[\w-\s]+")|([\w-]+(?:\.[\w-]+)*)|("[\w-\s]+")([\w-]+(?:\.[\w-]+)*))(@((?:[\w-]+\.)*\w[\w-]{0,66})\.([a-z]{2,6}(?:\.[a-z]{2})?)$)|(@\[?((25[0-5]\.|2[0-4][0-9]\.|1[0-9]{2}\.|[0-9]{1,2}\.))((25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\.){2}(25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\]?$)/i);
        if (!pattern.test(input["email"])) {
          isValid = false;
          errors["email"] = "Please enter valid email address.";
        }
      }

      if (!input["password"]) {
        isValid = false;
        errors["password"] = "Please enter your password.";
      }

      if (!input["confirm_password"]) {
        isValid = false;
        errors["confirm_password"] = "Please enter your confirm password.";
      }

      if (typeof input["password"] !== "undefined" && typeof input["confirm_password"] !== "undefined") {

        if (input["password"] !== input["confirm_password"]) {
          isValid = false;
          errors["password"] = "Passwords don't match.";
        }
      } 

      this.setState({
        errors: errors
      });

      return isValid;
  }

  render() {
    return (
      <div>
      <img src={bg} alt="Logo" widht="1440" className = "signup-bg" />
          <Container>
          <Form>
            <h1>Sign Up</h1>
            <Form.Group >
              <Form.Label>Username</Form.Label>
              <Form.Control type="username" placeholder="Enter Username" onChange={this.handleChange} value={this.state.input.name}/>
              <Form.Text style={{color: "red", fontSize: "12px"}} >
              {this.state.errors.username}
              </Form.Text>
            </Form.Group>

            <Form.Group >
              <Form.Label>Password</Form.Label>
              <Form.Control type="password" placeholder="Enter password" onChange={this.handleChange}/>
              <Form.Text style={{color: "red", fontSize: "12px"}} >
              {this.state.errors.password}
              </Form.Text>
            </Form.Group>

            <Button 
            style={{display:"flex", alignItems:"center", justifyContent:"center"}}
            variant="primary" type="submit">
              Submit
            </Button>
           </Form>
          </Container>



      </div>
    );
  }
}

export default SignUp;
